$(function () {
    // 必要的全局对象
    var $window = $(window);
    var $body = $('body');

    // loader
    var resources = [// 需要预加载的资源
    '../xinhuaTalking/index-assets/bg.jpg', '../xinhuaTalking/index-assets/cover-logo.png', '../xinhuaTalking/index-assets/scenes-1-btn-1-active-bg.png', '../xinhuaTalking/index-assets/scenes-1-btn-1-bg.png', '../xinhuaTalking/index-assets/demo-pic-0.png', '../xinhuaTalking/index-assets/demo-pic-1.png', '../xinhuaTalking/index-assets/demo-pic-2.png', '../xinhuaTalking/index-assets/demo-pic-3.png', '../xinhuaTalking/index-assets/demo-pic-4.png'];

    var loader = new resLoader({
        resources: resources,
        onStart: function onStart(total) {
            // console.log('start:' + total);
        },
        onProgress: function onProgress(current, total) {
            // console.log(current + '/' + total);
            // var percent = current / total * 100;
            // $('.progressbar').css('width', percent + '%');
            // $('.progresstext .current').text(current);
            // $('.progresstext .total').text(total);
        },
        onComplete: function onComplete(total) {
            // cover
            cover.init();
            // xinhuaTalking
            xinhuaTalking.init();
        }
    });

    // cover
    var cover = {
        $cover: $('#cover'),
        timeout: null,
        init: function init() {
            console.log('cover mod:', this.$cover.length);
            if (this.$cover.length) {
                this.coverClear();
                this.coverTimeout();
                this.coverClick();
            }
        },
        coverClick: function coverClick() {
            var _this = this;
            _this.$cover.on('click', function () {
                TweenMax.killAll();
                _this.coverHide();
                _this.coverClear();
                console.log('click', _this.timeout);
            });
        },
        coverClear: function coverClear() {
            clearTimeout(this.timeout);
            this.timeout = null;
        },
        coverTimeout: function coverTimeout() {
            var _this = this;
            _this.timeout = setTimeout(function () {
                _this.coverHide();
            }, 200);
        },
        coverHide: function coverHide() {
            console.log('coverHide');
            var _this = this;
            var time = 4;
            $body.addClass('overflow-hidden');
            // anis
            TweenMax.to(_this.$cover, time, {
                scale: 12,
                opacity: 0,
                ease: Power4.easeInOut,
                // ease: SlowMo.ease.config(0.7, 0.7, false),
                onStart: function onStart() {
                    _this.$cover.off('click');
                },
                onComplete: function onComplete() {
                    _this.$cover.remove();
                    $body.removeClass('overflow-hidden');
                    console.log('remove');
                }
            });
        }
    };

    var xinhuaTalking = {
        $nav: $('#nav'),
        $scenes1: $('#scenes-1'),
        navHeight: 67,
        activeIndex: 0,
        init: function init() {
            var _this = this;

            // getSize
            _this.getSize();

            // nav
            _this.navHeight = this.$nav.height();
            $window.on('resize', function () {
                if (_this.activeIndex < 1) {
                    _this.getSize();
                }
                _this.navStatus(_this.activeIndex);
            });

            // swiper
            _this.scenesMain = new Swiper('#main', {
                speed: 1000,
                hashnav: true,
                direction: 'vertical',
                keyboardControl: true,
                mousewheelControl: true,
                // onSlideChangeStart
                // onInit: callback.onInit,
                onSlideChangeStart: function onSlideChangeStart(swiper) {
                    console.log(swiper.activeIndex);
                    _this.activeIndex = swiper.activeIndex;
                    _this.navStatus(swiper.activeIndex);
                }
            });

            _this.scenes_1 = new Swiper('#scenes-1', {
                autoplay: 8000, //可选选项，自动滑动
                loop: true,
                parallax: true,
                pagination: '#scenes-1 .swiper-pagination',
                prevButton: '#scenes-1 .swiper-button-prev',
                nextButton: '#scenes-1 .swiper-button-next',
                paginationClickable: true,
                speed: 3000,
                onInit: function onInit(swiper) {
                    _this.zoom();
                    _this.qrcode();
                }
            });
        },
        getSize: function getSize() {
            this.size = {
                height: $window.height(),
                width: $window.width()
            };
        },
        qrcode: function qrcode() {
            var qrcodes = this.$scenes1.find('.scenes-1-qrcode');
            qrcodes.each(function (i, e) {
                var $e = $(e);
                $e.qrcode({
                    correctLevel: 1,
                    // background: "#999",
                    foreground: "#333", //"#0099ff"
                    width: 108,
                    height: 108,
                    text: $.trim($e.find('.scenes-1-qrcode-url').text())
                });
            });
        },
        zoom: function zoom() {
            var _this2 = this;

            var tags = [{
                tag: '.scenes-1-logo'
            }, {
                tag: '.scenes-1-title-top'
            }, {
                tag: '.scenes-1-title-1'
            }, {
                tag: '.scenes-1-title-4'
            }, {
                tag: '.scenes-1-content'
            }];
            var ratio = function ratio(num) {
                return Math.round(num * _this2.size.height / 1080);
            };
            var set = function set(opt) {
                var css = {
                    top: ratio(opt.num)
                };
                if (opt.type === 'height') {
                    css = {
                        height: ratio(opt.num)
                    };
                }
                opt.tag.css(css);
            };
            var init = function init() {
                _this2.getSize();
                for (var i = 0, j = tags.length; i < j; i++) {
                    if (!tags[i].num) {
                        tags[i].tag = _this2.$scenes1.find(tags[i].tag);
                        if (tags[i].type !== 'height') {
                            tags[i].num = tags[i].tag.offset().top;
                        } else {
                            tags[i].num = tags[i].tag.outerHeight();
                        }
                    }
                    set(tags[i]);
                }
            };
            $window.on('resize', function () {
                init();
            });
            init();
        },
        navPos: function navPos(num, time, callback) {
            TweenMax.to(this.$nav, time, {
                top: num,
                bottom: 'auto',
                onComplete: callback || null
            });
        },
        navStatus: function navStatus(activeIndex) {
            var _this = this;
            if (activeIndex < 1) {
                _this.navPos(_this.size.height - _this.navHeight, 0.6, function () {
                    _this.$nav.removeClass('nav-isTop');
                });
            } else {
                _this.navPos(0, 0.6, function () {
                    _this.$nav.addClass('nav-isTop');
                });
            }
        }
    };

    // 读取开始
    loader.start();
});
