$(function () {

    var $window = $(window);
    var $body = $('body');

    var xinhuaTalking = {
        activeIndex: 0,
        $nav: $('#nav'),
        $cover: $('#cover'),
        navHeight: 67,
        timeout: null,
        init: function init() {
            var _this = this;
            // getSize
            _this.getSize();

            // cover
            _this.coverInit();

            // nav
            $window.on('resize', function () {
                _this.getSize();
                _this.navStatus(_this.activeIndex);
            });

            _this.navHeight = this.$nav.height();
            _this.navPos(_this.size.height - _this.navHeight, 0);

            // swiper
            _this.scenesMain = _this.scenesMain();
            _this.scenes[1] = _this.scenes(1);
            _this.scenes[2] = _this.scenes(2);
            _this.scenes[3] = _this.scenes(3);
        },
        getSize: function getSize() {
            this.size = {
                height: $window.height(),
                width: $window.width()
            };
            return this.size;
        },
        navPos: function navPos(num, time) {
            TweenMax.to(this.$nav, time, {
                top: num
            });
            // return this.$nav;
        },
        navStatus: function navStatus(activeIndex) {
            if (activeIndex < 1) {
                this.navPos(this.size.height - this.navHeight, 0.4);
            } else {
                this.navPos(0, 0.4);
            }
        },
        coverInit: function coverInit(time) {
            console.log(this.$cover.length);
            if (this.$cover.length) {
                this.coverClear();
                this.coverTimeout();
                this.coverClick();
            }
        },
        coverClick: function coverClick() {
            var _this = this;
            _this.$cover.on('click', function () {
                TweenMax.killAll();
                _this.coverHide();
                _this.coverClear();
                console.log('click', _this.timeout);
            });
        },
        coverClear: function coverClear() {
            clearTimeout(this.timeout);
            this.timeout = null;
        },
        coverTimeout: function coverTimeout(time) {
            var _this = this;
            _this.timeout = setTimeout(function () {
                _this.coverHide();
            }, time || 2000);
        },
        coverHide: function coverHide() {
            console.log('coverHide');
            var _this = this;
            var time = 4;
            $body.addClass('overflow-hidden');
            // anis
            TweenMax.to(_this.$cover, time, {
                scale: 12,
                opacity: 0,
                ease: Power1.easeIn,
                onStart: function onStart() {
                    _this.$cover.off('click');
                },
                onComplete: function onComplete() {
                    _this.$cover.remove();
                    $body.removeClass('overflow-hidden');
                    console.log('remove');
                }
            });
        },
        scenesMain: function scenesMain() {
            var _this = this;
            return new Swiper('#main', {
                // loop: true,
                hashnav: true,
                direction: 'vertical',
                keyboardControl: true,
                mousewheelControl: true,
                // direction: 'vertical',
                // mousewheelForceToAxis: true,
                onSlideChangeStart: function onSlideChangeStart(swiper) {
                    _this.activeIndex = swiper.activeIndex;
                    // console.log(swiper.activeIndex);
                    _this.navStatus(_this.activeIndex);
                }
            });
        },
        scenes: function scenes(num) {
            return new Swiper('#scenes-' + num, {
                // autoplay: 5000, //可选选项，自动滑动
                pagination: '#scenes-' + num + ' .swiper-pagination',
                prevButton: '#scenes-' + num + ' .swiper-button-prev',
                nextButton: '#scenes-' + num + ' .swiper-button-next'
            });
        }
    };

    xinhuaTalking.init();

    return xinhuaTalking;
});
