$(function () {
    // 必要的全局对象
    var $window = $(window);
    var $body = $('body');

    // cover
    var cover = {
        $cover: $('#cover'),
        timeout: null,
        init: function init() {
            console.log('cover mod:', this.$cover.length);
            if (this.$cover.length) {
                this.coverClear();
                this.coverTimeout();
                this.coverClick();
            }
        },
        coverClick: function coverClick() {
            var _this = this;
            _this.$cover.on('click', function () {
                TweenMax.killAll();
                _this.coverHide();
                _this.coverClear();
                console.log('click', _this.timeout);
            });
        },
        coverClear: function coverClear() {
            clearTimeout(this.timeout);
            this.timeout = null;
        },
        coverTimeout: function coverTimeout() {
            var _this = this;
            _this.timeout = setTimeout(function () {
                _this.coverHide();
            }, 2000);
        },
        coverHide: function coverHide() {
            console.log('coverHide');
            var _this = this;
            var time = 4;
            $body.addClass('overflow-hidden');
            // anis
            TweenMax.to(_this.$cover, time, {
                scale: 12,
                opacity: 0,
                ease: Power1.easeIn,
                onStart: function onStart() {
                    _this.$cover.off('click');
                },
                onComplete: function onComplete() {
                    _this.$cover.remove();
                    $body.removeClass('overflow-hidden');
                    console.log('remove');
                }
            });
        }
    };

    var xinhuaTalking = {
        $nav: $('#nav'),
        navHeight: 67,
        activeIndex: 0,
        init: function init() {
            var _this = this;

            // getSize
            _this.getSize();

            // nav
            _this.navHeight = this.$nav.height();
            $window.on('resize', function () {
                if (_this.activeIndex < 1) {
                    _this.getSize();
                }
                _this.navStatus(_this.activeIndex);
            });

            // swiper
            _this.scenesMain = _this.scenesMain(1000);
            _this.scenes[1] = _this.scenes(1);
            // _this.scenes[2] = _this.scenes(2);
            // _this.scenes[3] = _this.scenes(3);
        },
        getSize: function getSize() {
            this.size = {
                height: $window.height(),
                width: $window.width()
            };
        },
        navPos: function navPos(num, time, callback) {
            TweenMax.to(this.$nav, time, {
                top: num,
                bottom: 'auto',
                onComplete: callback || null
            });
        },
        navStatus: function navStatus(activeIndex) {
            var _this = this;
            if (activeIndex < 1) {
                _this.navPos(_this.size.height - _this.navHeight, 0.6, function () {
                    _this.$nav.removeClass('isTop');
                });
            } else {
                _this.navPos(0, 0.6, function () {
                    _this.$nav.addClass('isTop');
                });
            }
        },
        scenesMain: function scenesMain(time) {
            var _this = this;
            return new Swiper('#main', {
                speed: time || 800,
                // hashnav: true,
                direction: 'vertical',
                keyboardControl: true,
                mousewheelControl: true,
                // onSlideChangeStart
                onTransitionStart: function onTransitionStart(swiper) {
                    console.log(swiper.activeIndex);
                    _this.activeIndex = swiper.activeIndex;
                    _this.navStatus(swiper.activeIndex);
                }
            });
        },
        scenes: function scenes(num) {
            return new Swiper('#scenes-' + num, {
                // autoplay: 5000, //可选选项，自动滑动
                loop: true,
                parallax: true,
                pagination: '#scenes-' + num + ' .swiper-pagination',
                prevButton: '#scenes-' + num + ' .swiper-button-prev',
                nextButton: '#scenes-' + num + ' .swiper-button-next',
                paginationClickable: true,
                speed: 2000
            });
        }
    };

    // cover
    cover.init();
    // xinhuaTalking
    xinhuaTalking.init();
});
